import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter"

function Expenses(props) {
  const [selectedYear, setSelectedYear] = useState(2019);

  const selectedYearChangeHandler = (selectedYear) => {
    setSelectedYear(selectedYear);
  }

  const filteredExpenses = props.items.filter(item => item.date.getFullYear().toString() === selectedYear);

  let expenseContent = <p>No expenses found.</p>;

  if (filteredExpenses.length > 0) {
    expenseContent = filteredExpenses.map((expense) =>  
    (<ExpenseItem
      key={expense.id}
      title={expense.title}
      amount={expense.amount}
      date={expense.date}
    />));
  }

  return (
    <div>
      <Card className="expenses">
      <ExpensesFilter onSelectedYearChange={selectedYearChangeHandler} selected={selectedYear} />
      {props.items.filter(item => item.date.getFullYear().toString() === selectedYear)
        .map((expense) =>  
          (<ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />)
        )
      }
      </Card>
    </div>
  );
}

export default Expenses;
